<section class="slice sct-color-1" style="padding-bottom: 0rem !important;padding-top:  0rem !important;" >    <div class="container">        <div class="section-title section-title--style-1 text-center" style="margin-bottom: 0rem !important;">            <h3 class="section-title-inner">            <span><b>HAPPY STORIES</b></span>            </h3>                </div>            <div class="swiper-js-container">            <div class="swiper-container" data-swiper-autoplay="true" data-swiper-items="4" data-swiper-space-between="20" data-swiper-md-items="3" data-swiper-md-space-between="20" data-swiper-sm-items="2" data-swiper-sm-space-between="20" data-swiper-xs-items="1" data-swiper-xs-space-between="0">                <div class="swiper-wrapper pb-5" style="padding-bottom: 1rem !important;">                    <?php foreach ($happy_stories as $happy_storie): ?>                        <div class="swiper-slide z-depth-2-bottom" data-swiper-autoplay="2300">                            <div class="card">                                <div class="card-image">                                    <?php                                        $story_image = $happy_storie->image;                                        $images = json_decode($story_image, true);                                        if (file_exists('uploads/happy_story_image/'.$images[0]['thumb'])) {                                        ?>                                            <!-- <img src="<?=base_url()?>uploads/happy_story_image/<?=$images[0]['thumb']?>"> -->                                            <div class="home_shory" style="background-image: url('<?=base_url()?>uploads/happy_story_image/<?=$images[0]['thumb']?>')"></div>                                        <?php                                        }                                        else {                                        ?>                                            <!-- <img src="<?=base_url()?>uploads/happy_story_image/default_image.jpg"> -->                                            <div class="home_shory" style="background-image: url('<?=base_url()?>uploads/happy_story_image/default_image.jpg')"></div>                                        <?php                                        }                                    ?>                                </div>								<div class="card-body" style="padding: 0.5rem !important;" >                                    <small><em><?= date_format(date_create($happy_storie->post_time),"d, F Y")?> </em></small>                                    <h3 class="heading heading-5 strong-500 story_heading">                                    <a href="<?=base_url()?>home/stories/story_detail/<?=$happy_storie->happy_story_id?>" style="cursor: pointer !important;"><?=substr($happy_storie->title,0,23)?><?php if(strlen($happy_storie->title) > 23){echo '..';}?></a>                                    </h3>                                </div>                            </div>                        </div>                    <?php endforeach ?>                </div>                  </div>        </div>    </div></section><script>    $(document).ready(function() {        setTimeout(function() {            set_story_box_height();        }, 1000);    });    function set_story_box_height() {        var max_title = 0;        $('.swiper-slide .story_heading').each(function() {            var current_height = parseInt($(this).css('height'));            if (current_height >= max_title) {                max_title = current_height;            }        });        $('.swiper-slide .story_heading').css('height', max_title);    }</script>